const styles = {
    bubbleAlert: {
        backgroundColor: '#E9725A',
        borderRadius: '20px',
        color: '#fff',
        width: '20px',
        padding: '5px 10px',
        fontSize: '11px',

    }
};

class BubbleAlert extends Component {
    getNumber(n){
        if(!n) {return ''}
        return n > 9 ? '+9' : n
    }
  render() {
    return <span style={styles.bubbleAlert}>+9</span>;
  }
}

export default BubbleAlert;
